import React, { createContext, useReducer } from 'react';

// import the Reducer 

import AppReducer from './AppReducer';



const initialSate = {

    transactions:[

        {id : 1, description : "income 1 ", transactionAmount: 1000 },
        {id : 2, description : "expense 1 ", transactionAmount: -100 },
        {id : 3, description : "income 1 ", transactionAmount: 3000 },
        {id : 4, description : "expense 1 ", transactionAmount: -500 }


    ]
}

// create global context

export const  GlobalContext = createContext(initialSate);

// create a provider for global context 

export const GlobalProvider = ({ children }) => {

    const [state,dispatch] = useReducer(AppReducer,initialSate);

        return(

            <GlobalContext.Provider value={

                {
                        transactions : state.transactions
                }

            }> {children}

            </GlobalContext.Provider>
        )
}
